import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { postJson } from "../../lib/api";
import "../../styles/auth.css";

export default function OTPVerify() {
  const loc = useLocation();
  const navigate = useNavigate();
  const preEmail = (loc.state && loc.state.email) || "";

  const [email, setEmail] = useState(preEmail);
  const [otp, setOtp] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [okMsg, setOkMsg] = useState("");

  useEffect(() => {
    if (preEmail) setEmail(preEmail);
  }, [preEmail]);

  async function handleVerify(e) {
    e.preventDefault();
    setError("");
    setOkMsg("");

    if (!email || !otp) {
      setError("Email and OTP are required.");
      return;
    }

    setLoading(true);
    try {
      const { status, data } = await postJson("/api/auth/verify-otp", { email, otp });
      if (status === 200) {
        setOkMsg("Email verified — you can now log in.");
        // small delay for UX then redirect to login
        setTimeout(() => navigate("/login", { replace: true }), 800);
      } else {
        setError(data?.detail || data?.message || JSON.stringify(data));
      }
    } catch (err) {
      console.error("Verify OTP error:", err);
      setError("Network error — please try again.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h1 className="auth-title">Verify your email</h1>
        <p className="auth-sub">Enter the verification code sent to your email</p>

        <form className="auth-form" onSubmit={handleVerify}>
          <label className="field">
            <span className="label">Email</span>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" />
          </label>

          <label className="field">
            <span className="label">OTP</span>
            <input value={otp} onChange={(e) => setOtp(e.target.value)} placeholder="123456" />
          </label>

          {error && <div className="error" role="alert">{error}</div>}
          {okMsg && <div className="success" role="status">{okMsg}</div>}

          <button className="btn primary" type="submit" disabled={loading}>
            {loading ? "Verifying…" : "Verify"}
          </button>
        </form>
      </div>
    </div>
  );
}
