import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { postJson } from "../../lib/api";
import { useAuth } from "../../lib/authContext";
import "../../styles/auth.css";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const { login } = useAuth();
  const navigate = useNavigate();

  async function handleLogin(e) {
    e.preventDefault();
    setError("");

    if (!email || !password) {
      setError("Email and password are required.");
      return;
    }

    setLoading(true);
    try {
      // POST /api/auth/login — postJson uses credentials: "include" so refresh cookie will be handled by browser
      const { status, data } = await postJson("/api/auth/login", { email, password });

      if (status === 200) {
        // Expecting backend to return { access_token: "..." } and set refresh cookie HttpOnly
        const access = data?.access_token || data?.accessToken || data?.token || null;
        if (!access) {
          setError("Login succeeded but no access token returned by server.");
          setLoading(false);
          return;
        }

        // Save access token in auth context (in-memory)
        login(access);

        // Redirect to app home or dashboard
        navigate("/", { replace: true });
      } else if (status === 401) {
        setError(data?.detail || data?.message || "Invalid email or password.");
      } else if (status === 403) {
        setError(data?.detail || data?.message || "Email not verified. Please verify your email.");
      } else {
        setError(data?.detail || data?.message || JSON.stringify(data));
      }
    } catch (err) {
      console.error("Login error:", err);
      setError("Network error — please try again.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h1 className="auth-title">Welcome back</h1>
        <p className="auth-sub">Sign in to your account</p>

        <form className="auth-form" onSubmit={handleLogin}>
          <label className="field">
            <span className="label">Email</span>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="you@example.com"
              required
            />
          </label>

          <label className="field">
            <span className="label">Password</span>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </label>

          {error && <div className="error" role="alert">{error}</div>}

          <button className="btn primary" type="submit" disabled={loading}>
            {loading ? "Signing in…" : "Sign in"}
          </button>

          <div className="auth-row" style={{ marginTop: 12 }}>
            <a className="link" href="/forgot-password">Forgot password?</a>
            <span style={{ color: "#94a3b8" }}>•</span>
            <a className="link" href="/register">Create account</a>
          </div>
        </form>
      </div>
    </div>
  );
}
