import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { postJson } from "../../lib/api";
import { useAuth } from "../../lib/authContext";
import "../../styles/auth.css";

export default function Register() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    department: "",
    role: "employee",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [okMsg, setOkMsg] = useState("");

  function onChange(e) {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setError("");
    setOkMsg("");

    if (!form.name || !form.email || !form.password) {
      setError("Name, email and password are required.");
      return;
    }

    setLoading(true);
    try {
      const { status, data } = await postJson("/api/auth/register", form);
      if (status === 201 || status === 200) {
        // Backend usually responds with neutral message like "If that account exists..."
        setOkMsg(data?.message || "Registered — please check your email for the OTP.");
        // Navigate to OTP verify and pass email in location.state
        navigate("/verify-otp", { state: { email: form.email } });
      } else {
        setError(data?.detail || data?.message || JSON.stringify(data));
      }
    } catch (err) {
      console.error("Register error:", err);
      setError("Network error — please try again.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h1 className="auth-title">Create account</h1>
        <p className="auth-sub">Sign up to BragBoard</p>

        <form className="auth-form" onSubmit={handleSubmit}>
          <label className="field">
            <span className="label">Full name</span>
            <input name="name" value={form.name} onChange={onChange} placeholder="Your full name" />
          </label>

          <label className="field">
            <span className="label">Email</span>
            <input name="email" type="email" value={form.email} onChange={onChange} placeholder="you@example.com" />
          </label>

          <label className="field">
            <span className="label">Password</span>
            <input name="password" type="password" value={form.password} onChange={onChange} placeholder="Create a password" />
          </label>

          <label className="field">
            <span className="label">Department</span>
            <input name="department" value={form.department} onChange={onChange} placeholder="Engineering" />
          </label>

          <label className="field">
            <span className="label">Role</span>
            <select name="role" value={form.role} onChange={onChange}>
              <option value="employee">Employee</option>
              <option value="manager">Manager</option>
              <option value="admin">Admin</option>
            </select>
          </label>

          {error && <div className="error" role="alert">{error}</div>}
          {okMsg && <div className="success" role="status">{okMsg}</div>}

          <button className="btn primary" type="submit" disabled={loading}>
            {loading ? "Creating…" : "Create account"}
          </button>
        </form>
      </div>
    </div>
  );
}
