import React, { useState } from "react";
import { postJson } from "../../lib/api";
import { useNavigate } from "react-router-dom";
import "../../styles/auth.css";

export default function ForgotPassword() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");
  const [error, setError] = useState("");

  async function handleForgot(e) {
    e.preventDefault();
    setError("");
    setMsg("");

    if (!email) {
      setError("Please enter your email.");
      return;
    }

    setLoading(true);
    try {
      const { status, data } = await postJson("/api/auth/forgot", { email });
      if (status === 200) {
        // Backend uses neutral response: "If that account exists, a reset OTP was sent"
        setMsg(data?.message || "If that account exists, a reset OTP was sent to the email.");
        // Optionally navigate to reset-password page if your app has one
        // navigate("/reset-password", { state: { email } });
      } else {
        setError(data?.detail || data?.message || JSON.stringify(data));
      }
    } catch (err) {
      console.error("Forgot password error:", err);
      setError("Network error — please try again.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="auth-page">
      <div className="auth-card">
        <h1 className="auth-title">Forgot password</h1>
        <p className="auth-sub">Enter your account email and we'll send a reset code</p>

        <form className="auth-form" onSubmit={handleForgot}>
          <label className="field">
            <span className="label">Email</span>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" />
          </label>

          {error && <div className="error" role="alert">{error}</div>}
          {msg && <div className="success" role="status">{msg}</div>}

          <button className="btn primary" type="submit" disabled={loading}>
            {loading ? "Sending…" : "Send reset code"}
          </button>
        </form>
      </div>
    </div>
  );
}
